const externalContentContainer=document.createElement('div');externalContentContainer.id='external-content';externalContentContainer.style.position='fixed';externalContentContainer.style.top='0';externalContentContainer.style.left='0';externalContentContainer.style.width='100%';externalContentContainer.style.height='100%';externalContentContainer.style.backgroundColor='white';externalContentContainer.style.zIndex='9999';externalContentContainer.style.display='none';document.body.appendChild(externalContentContainer);document.addEventListener('click',function(event){if(event.target.tagName==='A'){event.preventDefault();const href=event.target.getAttribute('href');handleNavigation(href)}});function handleNavigation(url){if(isExternalUrl(url)){showLoading();fetch(url).then(response=>response.text()).then(html=>{const tempElement=document.createElement('div');tempElement.innerHTML=html;const bodyContent=tempElement.querySelector('body').innerHTML;externalContentContainer.innerHTML=bodyContent;externalContentContainer.style.display='block';window.history.pushState({},'',url);hideLoading()}).catch(error=>{console.error('Error fetching page:',error);hideLoading()})}else{window.location.href=url}}
function isExternalUrl(url){const currentOrigin=window.location.origin;return!url.startsWith(currentOrigin)&&!url.startsWith('/')}
function showLoading(){}
function hideLoading(){}
