console.log("PWA install script loaded");
(function() {
    let deferredPrompt;

    function initPWAInstall() {
        window.addEventListener('beforeinstallprompt', (e) => {
            console.log('beforeinstallprompt event fired');
            e.preventDefault();
            deferredPrompt = e;
        });
        checkInstallTrigger();
    }

    function checkInstallTrigger() {
        const urlParams = new URLSearchParams(window.location.search);
        if (urlParams.get('pwa_install') === 'true' && urlParams.get('platform') === 'android') {
            console.log('pwa_install parameter detected for Android');
            triggerInstall();
        }
    }

    function triggerInstall() {
        if (deferredPrompt) {
            console.log('Triggering PWA install prompt');
            deferredPrompt.prompt();
            deferredPrompt.userChoice.then((choiceResult) => {
                if (choiceResult.outcome === 'accepted') {
                    console.log('User accepted the PWA installation');
                } else {
                    console.log('User dismissed the PWA installation');
                }
                deferredPrompt = null;
            });
        } else {
            console.log('PWA installation prompt not available, attempting alternative methods');
            attemptAlternativeInstall();
        }
    }

    function attemptAlternativeInstall() {
        if ('serviceWorker' in navigator) {
            navigator.serviceWorker.register('/superpwa-sw.js')
                .then(function(registration) {
                    console.log('SuperPWA Service Worker registered', registration.scope);
                    showInstallDialog();
                })
                .catch(function(error) {
                    console.error('SuperPWA Service Worker registration failed:', error);
                    alert('Unable to install the app at this time. Please try again later.');
                });
        } else {
            alert('Your browser does not support PWA installation. Please try using a different browser.');
        }
    }

    function showInstallDialog() {
        const dialog = document.createElement('div');
        dialog.style.position = 'fixed';
        dialog.style.left = '0';
        dialog.style.top = '0';
        dialog.style.width = '100%';
        dialog.style.height = '100%';
        dialog.style.backgroundColor = 'rgba(0,0,0,0.5)';
        dialog.style.display = 'flex';
        dialog.style.justifyContent = 'center';
        dialog.style.alignItems = 'center';
        dialog.style.zIndex = '10000';

        const content = document.createElement('div');
        content.style.backgroundColor = 'white';
        content.style.padding = '20px';
        content.style.borderRadius = '10px';
        content.style.maxWidth = '80%';
        content.style.textAlign = 'center';

        const message = document.createElement('p');
        message.textContent = 'Installation process initiated. Your device security software may scan the app before installation, which could cause a brief delay. This is a normal security measure to ensure safe installation.';

        const installButton = document.createElement('button');
        installButton.textContent = 'Install App';
        installButton.style.backgroundColor = '#ff5757';
        installButton.style.color = 'white';
        installButton.style.border = 'none';
        installButton.style.padding = '10px 20px';
        installButton.style.borderRadius = '5px';
        installButton.style.marginTop = '15px';
        installButton.style.cursor = 'pointer';

        installButton.addEventListener('click', function() {
            // Here you would typically trigger the installation
            // Since we don't have access to the actual installation mechanism,
            // we'll just close the dialog for now
            document.body.removeChild(dialog);
        });

        content.appendChild(message);
        content.appendChild(installButton);
        dialog.appendChild(content);

        document.body.appendChild(dialog);
    }

    // Initialize when the DOM is fully loaded
    if (document.readyState === 'loading') {
        document.addEventListener('DOMContentLoaded', initPWAInstall);
    } else {
        initPWAInstall();
    }

    // Test function
    window.testPWAInstall = function() {
        triggerInstall();
    }
})();